import React,{useState, useEffect} from 'react'

function MyCountFun(){
	const [count,setCount]=useState(0)
	useEffect(()=>{
		const interval = setInterval(()=>{
			setCount(count+1)
		},1000)

		return ()=>clearInterval(interval)
	},[])
	return <span>{count}</span>
}

export default MyCountFun
