import App from 'next/app'
import 'antd/dist/antd.less'
class MyApp extends App{
	static async getInitialProps({Component}){
		const pageProps = await Component.getInitialProps()
		return {
			pageProps
		}
	}
	render() {
		const {Component}=this.props
		return (
			<Container>
				<Component {...pageProps}/>
			</Container>
		);
	}
}
export default App
