import React,{useState, useEffect, useReducer, memo} from 'react'


function countReducer(state,action){
	switch(action.type){
		case 'add':
		    return state+1
		case 'minus':
			return state-1
		default:
			return state    
	}
}

function MyCountFun(){
	// const [count,setCount]=useState(0)
	const [count, dispatchCount]=useReducer(countReducer,0)
	const [name,setName]=useState('majy')
	// useEffect(()=>{
	// 	let interval = setInterval(()=>{
	// 		dispatchCount({type:'minus'})
	// 		// setCount(count=>count+1)
	// 	},1000)

	// 	return ()=>clearInterval(interval)
	// },[])
	//dom更新到节点之后 useLayoutEffect更新之前
	useEffect(()=>{
		console.log("useEffect init")
		return ()=>{
			console.log("useEffect detach")
		}
	},[name])// 传入的值有变化才会重新执行useEffect里的事情

    const config = {
    	text:`count is ${count}`,
    	color:count>3?'red':'blue'
    }

	return (
		<div>
			<input value={name} onChange={e=>setName(e.target.value)}/>
			<button onClick={()=>dispatchCount({type:'minus'})}>{count}</button>
			<Child config={config} onButtonClick={()=>dispatchCount({type:'add'})}/>
		</div>
	)
}
function Child({onButtonClick,config}){
	console.log('child render')
	return (
		<button onClick={onButtonClick} style={{color: config.color}}>{config.text}</button>
	)
}

export default MyCountFun
